import React, {useRef} from 'react';

const DefaultInput = ({type, value, onChange, classOfContainer, placeholder}) => {
  const placeholderRef = useRef();

  return (
    <div className={`position-relative ${classOfContainer}`}>
      <input 
        type={type}
        value={value}
        onChange={(e) => {
          onChange(e.target.value);
          manipulatePlaceholderPosition(placeholderRef);
        }
      }
        className="default-input"
    />
    <span className="placeholder" ref={placeholderRef}>{placeholder}</span>
    </div>
  )
}

DefaultInput.defaultProps = {
  type:"text",
  classOfContainer:"",
  onChange:() => "",
  value:"",
  placeholder:""
}

function manipulatePlaceholderPosition(placeholder) {
  placeholder.current.style.color = "blue";
}

export {DefaultInput};
