import axios from 'axios';
import {useState, useRef} from 'react';

const DateForm = () => {

  const monthRef = useRef();
  const dayRef = useRef();
  const yearRef = useRef();

  const month = ["January", "February", "March", "April", "May", "June", 
  "July", "August", "September", "October", "November", "December"];
  const day = getDays();
  const year = getYears();

  const [hasAnErro, setErro] = useState("");

  return (
    <form 
      className="big-margin-bottom"
      onSubmit={(e) => {
        e.preventDefault();
        validateData(setErro, monthRef, dayRef, yearRef);
      }}
    >
      <div id="date-container" className="margin-bottom">
        <select ref={monthRef.current} id="month-select">{month.map(createOption)}</select>
        <select ref={dayRef.current} id="day-select">{day.map(createOption)}</select>
        <select ref={yearRef.current} id="year-select">{year.map(createOption)}</select>
      </div>
      <div className="small-text big-margin-bottom">
        You need to enter the date you were born
      </div>
      <div className="small-text margin-bottom">
        Use your own birthday, even if this account is for a business, 
        a pet, or something else
      </div>
      
      {hasAnErro !== "" && <p className="erro-text margin-bottom">{hasAnErro}</p>}

      <input 
        type="submit"
        value="next"
        className="main-button"
      />
    </form>
  )
}


const createOption = (element, index) => {
  return <option value={index} key={index}>{element}</option>;
}

function getDays() {
  const maxDays = 30;// in the end 31
  const days = new Array(maxDays);
  for(let i = 0; i <= maxDays; i++) { days[i] = i + 1; }
  return days;
}

function getYears() {
  const currentYear = getCurrentYear();
  let nextYear = currentYear - 120;
  const years = new Array(120);
  for(let i = 0; i <= 120; i++) {
    years[i] = nextYear++;
  }
  return years.reverse();
}

function getCurrentYear() {
  const d = new Date();
  const year = d.getFullYear();
  return year;
}


function validateData(setErro, month, day, year) {
  console.log(month)
  const erroInFrontEnd = validateDataInFrontEnd();
  if(erroInFrontEnd === "") return validateDataInServer(setErro);

}


function validateDataInFrontEnd() {

}


function validateDataInServer(setErro) {
  /*axios.post("", user).then((erro) => {
    if(erro === "") return "";
    setErro(erro);
  });*/
}

export default DateForm;
