import {useState} from 'react';
import DefaultInput from '../../../global_components/DefaultInput';
import axios from 'axios';

const LoginForm = () => {
  const [nameOrNumber, setNameOrNumber] = useState("");
  const [password, setPassword] = useState("");

  return (
    <form className="flex-column small-margin-bottom" 
    onSubmit={(e) => validateLogin(e, nameOrNumber, password)}>
      <DefaultInput
        value={nameOrNumber}
        onChange={setNameOrNumber}
        classOfContainer="smaller-margin-bottom"
        placeholder="Phone number, username, or email"
      />
      <DefaultInput
        type="password"
        value={password}
        onChange={setPassword}
        classOfContainer="smaller-margin-bottom"
        placeholder="password"
      />
      <input
        type="submit"
        value="Log in"
        className="main-button"
      />
    </form>
  )
}


function validateLogin(e, nameOrNumber, password) {
  e.preventDefault();
  axios.post("http://localhost:5000/users/login", 
  {email:nameOrNumber, password:password}).then((result) => {
    if(result.erro === undefined) console.log("oi")
  });
}

export default LoginForm;
